$(document).on("click",".single-image-update-modal",function(){let d="/admin/dashboard/galleries/image/edit/"+$(this).data("uuid"),a=$("#imageGalleryUpdateModal");axios.get(d).then(function(t){let{data:e}=t.data;a.find('input[name="alt_text"]').val(e.alt_text),a.find('input[name="is_active"]').prop("checked",e.is_active),a.find(".image-input-wrapper").css("background-image","url(/storage/"+e.path+")");let i=a.find("[name=uuid]");i.length?i.val(e.uuid):$("<input>").attr({type:"hidden",id:"uuid",name:"uuid",value:e.uuid}).appendTo("#imageGalleryUpdateModalForm"),a.modal("show")}).catch(function(t){error(t)})});
