$(document).on("input","#title",function(){let t=$(this),e=t.next().find(".character_count");e.text("Kalan karakter sayısı: "+(150-t.val().length)),t.val().length>150?(t.addClass("is-invalid"),e.addClass("text-danger")):(t.removeClass("is-invalid"),e.removeClass("text-danger"))});let n=KTUtil.getById("postUpdateButton");KTUtil.addEvent(n,"click",function(){n.disabled=!0,KTUtil.btnWait(n,"spinner spinner-right spinner-white pr-15","Lütfen bekleyiniz."),setTimeout(function(){let t=$("#postUpdateForm"),e=new FormData(t[0]),i=t.data("uuid");e.append("post_uuid",i);let s="/author/dashboard/posts/update";axios.post(s,e).then(function(a){a.status===200&&(t[0].reset(),success(a),setTimeout(function(){},2e3))}).catch(function(a){error(a)}).finally(function(){KTUtil.btnRelease(n),n.disabled=!1})},1e3)});
